(()=>{document.querySelector("body"),document.getElementById("time");const e=document.getElementById("location"),n=document.getElementById("temperature"),t=document.getElementById("description"),i=document.getElementById("feels"),m=(document.getElementById("humidity-image"),document.getElementById("humidity")),o=(document.getElementById("min-image"),document.getElementById("min")),d=(document.getElementById("max-image"),document.getElementById("max")),a=(document.getElementById("wind-image"),document.getElementById("wind")),c=(document.getElementById("visibility-image"),document.getElementById("visibility"));function r(e){return`${(Number(e)-273.15).toPrecision(3)} ℃`}function u(e){return`${(1.8*(Number(e)-273.15)+32).toPrecision(4)} ℉`}!async function(l){try{const l=await fetch("http://api.openweathermap.org/data/2.5/weather?q=London&APPID=eed84943e5486d8a117b70b7eaf2d745",{mode:"cors"}),y=await l.json();!function(l,y="celsius"){e.innerText=`${l.name}, ${l.sys.country}`,"celsius"===y?(n.innerText=r(l.main.temp),i.innerText=r(l.main.feels_like),o.innerText=r(l.main.temp_min),d.innerText=r(l.main.temp_max)):(n.innerText=u(l.main.temp),n.innerText=u(l.main.feels_like),o.innerText=u(l.main.temp_min),d.innerText=u(l.main.temp_max)),t.innerText=l.weather[0].main,m.innerText=`${l.main.humidity}% Humidity`,a.innerText=`${l.wind.speed} km/h`,c=`${(l.main.visibility/1e3).persision(1)}`}(y),console.log(y)}catch{console.log("oops")}}()})();